package day17;

public class WhileLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 100;
		int n2 = 140;

		while (n2 > n1) { // true
			System.out.println("N2 is greater");
		}

	}// It is a infinite Loop, because condition will always be true and never
	// never becomes fault
	// while loop executeion as long 

}
