package day8;

public class IfElseVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	int teamAScore = 5;
	int teamBScore = 9;
	
	   // 5            9
	if(teamAScore > teamBScore) {
		System.out.println("Team A won");
		System.out.println("Gp Team A");
	}else {
		System.out.println("Team B won");
		System.out.println("Go Teams");
	}
	
	
	int a = 1;
	int b = 2;
	
	if(b > a) {
		System.out.println("Number "+ b + " is greater" );
	}else {
		System.out.println("Number "+ a + " is greater");
	}
	int num = 1;
	
	if(num > 0) {
		System.out.println("positive"); 
	}
		
	if(num < 0) {
		System.out.println("negative");
	}
	
	if (num == 0) {
		System.out.println("zero");
	}
	}
	
	
	
	
	
	
	
}

